#include <bits/stdc++.h>
#define _ ios_base::sync_with_stdio(0);cin.tie(0);
using namespace std;


class FixedDiceGameDiv2 {
public:
	double getExpectation(int a, int b) {
		double res;
		long long sum=0, n=0, now;

		for (long long i = 2; i<=a; i++)
        {
            now = (i>b?b:i-1);
            sum+=(i*now);
            n+=now;
        }

		res = sum;
		res= res/(double)n;
		return res;
	}
};


